/**
 * Converts miles to kilometers.
 *
 * @param miles - Distance in miles.
 *
 * @returns The distance converted to kilometers.
 */
function milesToKilometers(miles: number): number {
  // This comment adheres to the rule by describing the conversion process without repeating type information.
  return miles * 1.60934
}

