/**
 * Checks if an email address is valid.
 */
function isValidEmail(email: string): boolean {
  // This comment adheres to the rule by focusing on the validation process, not the parameter or return types.
  const regex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/
  return regex.test(email)
}

