/**
 * Resets a form to its default values.
 *
 * @param formId - The ID of the form to reset.
 */
function resetForm(formId: string): void {
  // This comment adheres to the rule by focusing on the action taken, not the types of its parameters.
  const form = document.getElementById(formId) as HTMLFormElement
  form.reset()
}

