/**
 * Filters an array of numbers to only include even numbers.
 *
 * @param numbers - The array of numbers to filter.
 *
 * @returns An array containing only even numbers.
 */
function filterEvenNumbers(numbers: number[]): number[] {
  // This comment adheres to the rule by describing the filtering action without repeating type information.
  return numbers.filter((number) => number % 2 === 0)
}

