// Example 5: Transforming product details for display
interface ProductInput {
  id: string | number
  title: string
  price?: number
}

interface ProductDisplay {
  id: number
  title: string
  price: string // Price formatted as a string
}

function transformProductForDisplay(product: ProductInput): ProductDisplay {
  // Accepts a flexible input but ensures a consistent output format
  return {
    id: Number(product.id),
    title: product.title,
    price: product.price ? `$${product.price.toFixed(2)}` : 'N/A'
  }
}

