// Use defensive error handling with async/await
async function fetchData() {
  try {
    const response = await fetch('https://api.example.com/data')
    const data = await response.json()
    console.log(data)
  } catch (error) {
    console.error('Failed to fetch data', error)
  }
}
// This adheres to defensive programming by catching and handling errors in asynchronous operations.

