// Using native fetch with async/await to POST data to an API
async function postData(url = '', data = {}) {
  const response = await fetch(url, {
    method: 'POST',
    headers: {
      'Content-Type': 'application/json'
    },
    body: JSON.stringify(data)
  })
  return response.json()
}
// This code correctly adheres to the rule by using fetch with async/await for asynchronous operations.

