// Utilizing React hooks in a functional component
function Counter() {
  // Correct usage of useState hook within a functional component
  const [count, setCount] = useState(0)

  return (
    <div>
      <p>You clicked {count} times</p>
      <button onClick={() => setCount(count + 1)}>Click me</button>
    </div>
  )
}

