// Correct use of semantic variable names in a function calculating the hypotenuse
function calculateHypotenuse(
  oppositeSide: number,
  adjacentSide: number
): number {
  // "oppositeSide" and "adjacentSide" are descriptive, relating to triangle sides
  return Math.sqrt(oppositeSide ** 2 + adjacentSide ** 2)
}

